/**
 * 返回冥王星J2000直角坐标
 */
function pluto_coord($t)
{
    $XL0Pluto = XL0Pluto();
    $c0 = Constant::pi / 180 / 100000;
    $x = -1 + 2 * ($t * 36525 + 1825394.5) / 2185000;
    $T = $t / 100000000;
    $r = [0, 0, 0];
    for ($i = 0; $i < 9; $i++) {
        $ob = $XL0Pluto[$i];
        $N  = count($ob);
        $v  = 0;
        for ($j = 0; $j < $N; $j += 3) {
            $v += $ob[$j] * sin( $ob[$j + 1] * $T + $ob[$j + 2] * $c0 );
        }
        if ($i % 3 === 1) {
            $v *= $x;
        }
        if ($i % 3 === 2) {
            $v *= $x * $x;
        }
        $r[ floor($i / 3) ] += $v / 100000000;
    }
    $r[0] +=  9.922274 + 0.154154 * $x;
    $r[1] += 10.016090 + 0.064073 * $x;
    $r[2] += -3.947474 - 0.042746 * $x;
    return $r;
}